import React from "react";
import { Button } from "../Button/Button";
import "./current-item.css";

export const CurrentItem = ({ currentItem, handleClick }) => {
  return (
    <div className="card-item">
      <div className="card-img">
        <img src={currentItem.image} />
      </div>
      <div className="card-text">
        <h2 className="title">{currentItem.title}</h2>
        <h4 className="price">{currentItem.price}р</h4>
        <p>{currentItem.sostav}</p>
      </div>
      <div className="card-btn">
        <Button label="Выбрать" />
      </div>
      <div className="card-btn">
        <Button label="Назад" handleClick={handleClick} />
      </div>
    </div>
  );
};
